import React,{useState,useContext} from 'react'
import {UserContext} from '../UserContext'
function Searchx() {
    const [name,setName]=useState("");
    const {user2,setUser2}=useContext(UserContext);
    const handleChange=(e)=>{
        setName(e.target.value);
    }
    const handleClick=async e=>{
        e.preventDefault();
        const profile=await fetch(`https://codeforces.com/api/user.info?handles=${name}`);
        const profileJson=await profile.json();
        //console.log(profileJson);
        if(profileJson){
            setUser2(profileJson.result[0])
        }
    }
    return (
        <div style={{padding:"3rem"}}>
            <div >
            <form onSubmit={handleClick}>
                <div className="ui category search">
                    <div className="ui icon input">
                       <input className="prompt" type="text" value={name} onChange={handleChange} placeholder="Username..."/>
                       <i class="search icon"></i>
                    </div>
                </div>   
            </form>
            {/* <li>{data.rank}</li>
            <li>{data.organization}</li> */}  
        </div>
        <div class="ui list">
        <div class="item">
            <img class="ui avatar image" src={user2.avatar} alt="avatar"></img>
            <div class="content">
        <div class="header">{user2.firstName} {user2.lastName} ({user2.rank})</div>
            <div class="description"><b>{user2.organization}</b></div>
            <div class="description"></div>
            </div>
        </div>        
</div>
</div>


    )
}

export default Searchx
