import React,{useState,useContext} from 'react'
import {UserContext} from '../UserContext'
function Search() {
    const [name,setName]=useState("");
    const {user1,setUser1}=useContext(UserContext);
    const handleChange=(e)=>{
        setName(e.target.value);
    }
    const handleClick=async e=>{
        e.preventDefault();
        const profile=await fetch(`https://codeforces.com/api/user.info?handles=${name}`);
        const profileJson=await profile.json();
        //console.log(profileJson);
        if(profileJson){
            setUser1(profileJson.result[0])
        }
        const status=await fetch(`https://codeforces.com/api/user.status?handle=${name}`)
        const statusJson=await status.json();
        //console.log(statusJson);
    }
    return (
        <div style={{padding:"3rem"}}>
            <div >
            <form onSubmit={handleClick}>
                <div className="ui category search">
                    <div className="ui icon input">
                       <input className="prompt" type="text" value={name} onChange={handleChange} placeholder="Username..."/>
                       <i class="search icon"></i>
                    </div>
                </div>   
            </form>
            {/* <li>{data.rank}</li>
            <li>{data.organization}</li> */}  
        </div>
        <div class="ui list">
        <div class="item">
            <img class="ui avatar image" src={user1.avatar} alt="avatar"></img>
            <div class="content">
        <div class="header">{user1.firstName} {user1.lastName} ({user1.rank})</div>
            <div class="description"><b>{user1.organization}</b></div>
            </div>
        </div>        
</div>
</div>


    )
}

export default Search
